$.fn.dataTableExt.oPagination.extStyle = {

  "fnInit": function (oSettings, nPaging, fnCallbackDraw) {

    var oPaging = oSettings.oInstance.fnExtStylePagingInfo();

    nPrevious = $('<button/>', { 'class': 'paginate_button previous blue quanto-button small' , text : "Prev" });
    nNext = $('<button/>', { 'class': 'paginate_button next blue quanto-button small' , text : "Next" });
    nPageNumBox = $('<span />', { text : '1', 'class': 'paginate_current_page_label' });
    nPageOf = $('<span />', { text: ' of ' });
    nTotalPages = $('<span />', { class :  "paginate_total" , text : oPaging.iTotalPages });


    $(nPaging)
      .append(nPrevious)
      .append(nPageNumBox)
      .append(nPageOf)
      .append(nTotalPages)
      .append(nNext)

    nPrevious.click(function () {
      if( $(this).hasClass("disabled") )
        return;
      oSettings.oApi._fnPageChange(oSettings, "previous");
      fnCallbackDraw(oSettings);
    }).bind('selectstart', function () { return false; });

    nNext.click(function () {
      if( $(this).hasClass("disabled") )
        return;
      oSettings.oApi._fnPageChange(oSettings, "next");
      fnCallbackDraw(oSettings);
    }).bind('selectstart', function () { return false; });

    nPageNumBox.change(function () {
      var pageValue = parseInt($(this).val(), 10) - 1 ; // -1 because pages are 0 indexed, but the UI is 1
      var oPaging = oSettings.oInstance.fnPagingInfo();

      if(pageValue === NaN || pageValue<0 ){
        pageValue = 0;
      }else if(pageValue >= oPaging.iTotalPages ){
        pageValue = oPaging.iTotalPages -1;
      }
      oSettings.oApi._fnPageChange(oSettings, pageValue);
      fnCallbackDraw(oSettings);
    });

  },


  "fnUpdate": function (oSettings, fnCallbackDraw) {
    if (!oSettings.aanFeatures.p) {
      return;
    }

    var oPaging = oSettings.oInstance.fnExtStylePagingInfo();

    /* Loop over each instance of the pager */
    var an = oSettings.aanFeatures.p;

    $(an).find('.paginate_total').text(oPaging.iTotalPages);
    $(an).find('.paginate_current_page_label').text(oPaging.iPage+1);

    $(an).each(function(index,item) {

      var $item = $(item);

      if (oPaging.iPage == 0) {
        var prev = $item.find('.paginate_button.previous');
        prev.addClass("disabled");
      }else {
        var prev = $item.find('.paginate_button.previous');
        prev.removeClass("disabled");
      }

      if (oPaging.iPage+1 == oPaging.iTotalPages) {
        var next = $item.find('.paginate_button.next');
        next.addClass("disabled");
      }else {
        var next = $item.find('.paginate_button.next');
        next.removeClass("disabled");
      }
    });
  }
};
